    .nav-content {
      display: flex;
      align-items: center;
      justify-content: space-between;
    }

    .brand {
      display: flex;
      align-items: center;
      gap: 0.75rem;
      transition: all 0.3s ease;
      cursor: pointer;
    }

    .brand:hover {
      transform: translateY(-1px);
    }

    .logo-icon {
      width: 44px;
      height: 44px;
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      border-radius: 14px;
      display: flex;
      align-items: center;
      justify-content: center;
      color: white;
      box-shadow: 0 6px 20px rgba(102, 126, 234, 0.35);
      transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
      position: relative;
      overflow: hidden;
    }

    .logo-shine {
      position: absolute;
      top: -50%;
      left: -50%;
      width: 200%;
      height: 200%;
      background: linear-gradient(45deg, transparent 30%, rgba(255,255,255,0.4) 50%, transparent 70%);
      transform: translateX(-100%) translateY(-100%) rotate(45deg);
      transition: transform 0.6s ease;
    }

    .brand:hover .logo-shine {
      transform: translateX(100%) translateY(100%) rotate(45deg);
    }

    .brand:hover .logo-icon {
      transform: translateY(-2px) scale(1.05);
      box-shadow: 0 12px 35px rgba(102, 126, 234, 0.5);
    }

    .logo-icon svg {
      width: 20px;
      height: 20px;
      z-index: 1;
    }

    .brand-text {
      font-size: 1.6rem;
      font-weight: 800;
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      -webkit-background-clip: text;
      -webkit-text-fill-color: transparent;
      background-clip: text;
      letter-spacing: -0.5px;
    }

    .nav-links {
      display: flex;
      align-items: center;
      gap: 2.5rem;
    }

    .nav-link {
      color: #64748b;
      text-decoration: none;
      font-weight: 600;
      font-size: 0.95rem;
      position: relative;
      padding: 0.75rem 1rem;
      border-radius: 12px;
      transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
      cursor: pointer;
      overflow: hidden;
    }

    .nav-link span {
      position: relative;
      z-index: 2;
      transition: color 0.3s ease;
    }

    .nav-link-bg {
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      background: linear-gradient(135deg, rgba(102, 126, 234, 0.1) 0%, rgba(118, 75, 162, 0.1) 100%);
      transform: scaleX(0);
      transform-origin: left;
      transition: transform 0.3s cubic-bezier(0.4, 0, 0.2, 1);
      border-radius: 12px;
    }

    .nav-link::after {
      content: '';
      position: absolute;
      bottom: 4px;
      left: 50%;
      width: 0;
      height: 2px;
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
      transform: translateX(-50%);
      border-radius: 1px;
    }

    .nav-link:hover {
      color: #667eea;
      transform: translateY(-1px);
    }

    .nav-link:hover span {
      color: #667eea;
    }

    .nav-link:hover .nav-link-bg {
      transform: scaleX(1);
    }

    .nav-link:hover::after {
      width: 60%;
    }

    .btn-nav-login {
      background: none;
      border: 2px solid #e2e8f0;
      color: #64748b;
      font-weight: 600;
      padding: 0.75rem 1.5rem;
      border-radius: 14px;
      transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
      position: relative;
      overflow: hidden;
      cursor: pointer;
    }

    .btn-nav-login span {
      position: relative;
      z-index: 2;
      transition: color 0.3s ease;
    }

    .btn-ripple {
      position: absolute;
      top: 50%;
      left: 50%;
      width: 0;
      height: 0;
      background: radial-gradient(circle, rgba(102, 126, 234, 0.2) 0%, transparent 70%);
      transform: translate(-50%, -50%);
      transition: all 0.4s ease;
      border-radius: 50%;
    }

    .btn-nav-login:hover {
      border-color: #667eea;
      color: #667eea;
      transform: translateY(-2px);
      box-shadow: 0 8px 25px rgba(102, 126, 234, 0.2);
    }

    .btn-nav-login:hover span {
      color: #667eea;
    }

    .btn-nav-login:hover .btn-ripple {
      width: 120%;
      height: 120%;
    }

    .btn-nav-login:active {
      transform: translateY(-1px);
    }

    .btn-nav-signup {
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      color: white;
      border: none;
      padding: 0.75rem 2rem;
      border-radius: 25px;
      font-weight: 700;
      font-size: 0.95rem;
      transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
      position: relative;
      overflow: hidden;
      box-shadow: 0 6px 20px rgba(102, 126, 234, 0.35);
      cursor: pointer;
    }

    .btn-nav-signup span {
      position: relative;
      z-index: 2;
    }

    .btn-glow {
      position: absolute;
      top: -2px;
      left: -2px;
      right: -2px;
      bottom: -2px;
      background: linear-gradient(135deg, #667eea 0%, #764ba2 50%, #f093fb 100%);
      border-radius: 27px;
      opacity: 0;
      transition: opacity 0.3s ease;
      z-index: 1;
    }

    .btn-nav-signup::before {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      background: linear-gradient(135deg, rgba(255,255,255,0.2) 0%, transparent 100%);
      opacity: 0;
      transition: opacity 0.3s ease;
      z-index: 2;
      border-radius: 25px;
    }

    .btn-nav-signup:hover {
      transform: translateY(-3px) scale(1.02);
      box-shadow: 0 15px 40px rgba(102, 126, 234, 0.6);
    }

    .btn-nav-signup:hover .btn-glow {
      opacity: 1;
      animation: pulse 2s infinite;
    }

    .btn-nav-signup:hover::before {
      opacity: 1;
    }

    .btn-nav-signup:active {
      transform: translateY(-1px) scale(0.98);
    }

    @keyframes pulse {
      0%, 100% { opacity: 0.7; }
      50% { opacity: 1; }
    }

    /* Hero Section */
    .hero-enhanced {
      position: relative;
      min-height: 100vh;
      display: flex;
      align-items: center;
      overflow: hidden;
    }

    .hero-bg {
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      z-index: -1;
    }

    .hero-gradient {
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      background: linear-gradient(135deg, #667eea 0%, #764ba2 50%, #f093fb 100%);
      opacity: 0.1;
    }

    .hero-pattern {
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      background-image: radial-gradient(circle at 25% 25%, rgba(102, 126, 234, 0.1) 0%, transparent 50%),
                        radial-gradient(circle at 75% 75%, rgba(118, 75, 162, 0.1) 0%, transparent 50%);
    }

    .hero-content {
      flex: 1;
      max-width: 600px;
      padding-top: 100px;
    }

    .hero-badge {
      display: inline-flex;
      align-items: center;
      gap: 0.5rem;
      background: rgba(102, 126, 234, 0.1);
      color: #667eea;
      padding: 0.5rem 1rem;
      border-radius: 50px;
      font-size: 0.875rem;
      font-weight: 500;
      margin-bottom: 2rem;
      border: 1px solid rgba(102, 126, 234, 0.2);
    }

    .badge-icon {
      font-size: 1rem;
    }

    .hero-title {
      font-size: 4rem;
      font-weight: 800;
      line-height: 1.1;
      margin-bottom: 1.5rem;
      color: #0f172a;
      min-height: 4.4rem;
    }

    .typewriter-text {
      background: linear-gradient(135deg, #667eea 0%, #764ba2 50%, #f093fb 100%);
      -webkit-background-clip: text;
      -webkit-text-fill-color: transparent;
      background-clip: text;
      background-size: 200% 200%;
      animation: gradientShift 3s ease-in-out infinite;
    }

    .cursor {
      color: #667eea;
      font-weight: 100;
      animation: fadeInOut 1s infinite;
    }

    .cursor.blink {
      animation: blink 1s infinite;
    }

    @keyframes gradientShift {
      0%, 100% { background-position: 0% 50%; }
      50% { background-position: 100% 50%; }
    }

    @keyframes fadeInOut {
      0%, 50% { opacity: 1; }
      51%, 100% { opacity: 0; }
    }

    @keyframes blink {
      0%, 50% { opacity: 1; }
      51%, 100% { opacity: 0; }
    }

    .hero-subtitle {
      font-size: 1.25rem;
      color: #64748b;
      line-height: 1.6;
      margin-bottom: 3rem;
    }

    .hero-actions {
      display: flex;
      align-items: center;
      gap: 1.5rem;
      margin-bottom: 4rem;
    }

    .btn-primary-large {
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      color: white;
      border: none;
      padding: 1rem 2rem;
      border-radius: 16px;
      font-size: 1.125rem;
      font-weight: 600;
      display: flex;
      align-items: center;
      gap: 0.75rem;
      transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
      position: relative;
      overflow: hidden;
    }

    .interactive-btn {
      position: relative;
      overflow: hidden;
    }

    .btn-primary-large:hover {
      transform: translateY(-3px) scale(1.02);
      box-shadow: 0 20px 40px rgba(102, 126, 234, 0.5);
    }

    .btn-primary-large:hover .btn-icon {
      transform: translateX(4px);
    }

    .btn-particles {
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      background: radial-gradient(circle at center, rgba(255,255,255,0.3) 0%, transparent 70%);
      opacity: 0;
      transition: opacity 0.3s ease;
    }

    .btn-primary-large:hover .btn-particles {
      opacity: 1;
      animation: particleFloat 2s infinite;
    }

    @keyframes particleFloat {
      0%, 100% { transform: translateY(0) scale(1); }
      50% { transform: translateY(-2px) scale(1.05); }
    }

    .btn-icon {
      width: 20px;
      height: 20px;
    }

    .btn-secondary-large {
      background: rgba(255, 255, 255, 0.9);
      border: 2px solid rgba(102, 126, 234, 0.2);
      color: #64748b;
      padding: 1rem 2rem;
      border-radius: 16px;
      font-size: 1.125rem;
      font-weight: 600;
      display: flex;
      align-items: center;
      gap: 0.75rem;
      transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
      backdrop-filter: blur(10px);
      position: relative;
      overflow: hidden;
    }

    .btn-secondary-large:hover {
      border-color: #667eea;
      color: #667eea;
      transform: translateY(-2px);
      box-shadow: 0 15px 35px rgba(102, 126, 234, 0.2);
      background: rgba(255, 255, 255, 1);
    }

    .play-button-inner {
      width: 32px;
      height: 32px;
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      border-radius: 50%;
      display: flex;
      align-items: center;
      justify-content: center;
      transition: all 0.3s ease;
    }

    .play-icon {
      width: 16px;
      height: 16px;
      color: white;
      margin-left: 2px;
    }

    .btn-secondary-large:hover .play-button-inner {
      transform: scale(1.1);
      box-shadow: 0 5px 15px rgba(102, 126, 234, 0.4);
    }

    .play-icon {
      width: 20px;
      height: 20px;
    }

    .hero-stats {
      display: flex;
      gap: 3rem;
    }

    .stat {
      text-align: center;
    }

    .stat-number {
      font-size: 2rem;
      font-weight: 700;
      color: #0f172a;
      margin-bottom: 0.25rem;
    }

    .stat-label {
      color: #64748b;
      font-size: 0.875rem;
    }

    /* Hero Visual */
    .hero-visual {
      flex: 1;
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;
      padding-top: 100px;
      position: relative;
    }

    .floating-bg-elements {
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      pointer-events: none;
      overflow: hidden;
    }

    .bg-element-1 {
      position: absolute;
      top: 10%;
      right: 10%;
      width: 100px;
      height: 100px;
      background: linear-gradient(135deg, rgba(102, 126, 234, 0.1) 0%, rgba(118, 75, 162, 0.1) 100%);
      border-radius: 50%;
      animation: float 6s ease-in-out infinite;
    }

    .bg-element-2 {
      position: absolute;
      bottom: 20%;
      left: 15%;
      width: 60px;
      height: 60px;
      background: linear-gradient(135deg, rgba(240, 147, 251, 0.15) 0%, rgba(245, 87, 108, 0.15) 100%);
      border-radius: 30%;
      animation: float 8s ease-in-out infinite reverse;
    }

    .bg-element-3 {
      position: absolute;
      top: 60%;
      right: 30%;
      width: 80px;
      height: 80px;
      background: linear-gradient(135deg, rgba(16, 185, 129, 0.1) 0%, rgba(5, 150, 105, 0.1) 100%);
      border-radius: 20%;
      animation: float 10s ease-in-out infinite;
    }

    @keyframes float {
      0%, 100% { transform: translateY(0px) rotate(0deg); }
      33% { transform: translateY(-20px) rotate(120deg); }
      66% { transform: translateY(10px) rotate(240deg); }
    }

    .hero-showcase {
      position: relative;
      width: 100%;
      max-width: 600px;
      margin: 0 auto;
    }

    .editor-preview {
      background: white;
      border-radius: 20px;
      box-shadow: 0 20px 60px rgba(0, 0, 0, 0.15);
      overflow: hidden;
      border: 1px solid rgba(0, 0, 0, 0.1);
    }

    .editor-header {
      background: #f8fafc;
      padding: 1rem 1.5rem;
      display: flex;
      justify-content: space-between;
      align-items: center;
      border-bottom: 1px solid #e2e8f0;
    }

    .editor-tabs {
      display: flex;
      gap: 1rem;
    }

    .tab {
      padding: 0.5rem 1rem;
      border-radius: 8px;
      font-size: 0.875rem;
      font-weight: 500;
      color: #64748b;
      cursor: pointer;
      transition: all 0.3s ease;
    }

    .tab.active {
      background: white;
      color: #667eea;
      box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
    }

    .editor-actions {
      display: flex;
      gap: 0.5rem;
    }

    .action-dot {
      width: 12px;
      height: 12px;
      border-radius: 50%;
      cursor: pointer;
    }

    .action-dot.save { background: #10b981; }
    .action-dot.minimize { background: #f59e0b; }
    .action-dot.close { background: #ef4444; }

    .editor-content {
      display: flex;
      min-height: 400px;
    }

    .signature-canvas {
      flex: 2;
      padding: 2rem;
      background: #fafbfc;
      position: relative;
      display: flex;
      align-items: center;
      justify-content: center;
    }

    .editor-sidebar {
      flex: 1;
      background: white;
      padding: 1.5rem;
      border-left: 1px solid #e2e8f0;
    }

    .tool-section {
      margin-bottom: 2rem;
    }

    .tool-title {
      font-size: 0.875rem;
      font-weight: 600;
      color: #374151;
      margin-bottom: 1rem;
    }

    .color-palette {
      display: flex;
      gap: 0.5rem;
      flex-wrap: wrap;
    }

    .color-dot {
      width: 32px;
      height: 32px;
      border-radius: 8px;
      cursor: pointer;
      border: 2px solid transparent;
      transition: all 0.3s ease;
    }

    .color-dot:hover {
      border-color: #667eea;
      transform: scale(1.1);
    }

    .font-options {
      display: flex;
      flex-direction: column;
      gap: 0.5rem;
    }

    .font-option {
      padding: 0.75rem;
      border-radius: 8px;
      background: #f8fafc;
      font-size: 0.875rem;
      cursor: pointer;
      transition: all 0.3s ease;
    }

    .font-option.active {
      background: #667eea;
      color: white;
    }

    .font-option:hover:not(.active) {
      background: #e2e8f0;
    }

    .signature-card {
      background: white;
      border-radius: 12px;
      padding: 1.5rem;
      box-shadow: 0 8px 25px rgba(0, 0, 0, 0.1);
      transition: all 0.6s cubic-bezier(0.4, 0, 0.2, 1);
      opacity: 0;
      transform: scale(0.9);
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%) scale(0.9);
      width: 280px;
    }

    .signature-card.active {
      opacity: 1;
      transform: translate(-50%, -50%) scale(1);
      box-shadow: 0 15px 40px rgba(102, 126, 234, 0.3);
      z-index: 2;
    }

    .signature-content {
      display: flex;
      align-items: center;
      gap: 1rem;
    }

    .avatar {
      width: 48px;
      height: 48px;
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      border-radius: 50%;
      position: relative;
      overflow: hidden;
    }

    .pulse-animation {
      animation: pulse 2s infinite;
    }

    @keyframes pulse {
      0% { box-shadow: 0 0 0 0 rgba(102, 126, 234, 0.4); }
      70% { box-shadow: 0 0 0 10px rgba(102, 126, 234, 0); }
      100% { box-shadow: 0 0 0 0 rgba(102, 126, 234, 0); }
    }

    .social-icons {
      display: flex;
      gap: 0.5rem;
      margin-top: 0.5rem;
    }

    .social-dot {
      width: 8px;
      height: 8px;
      border-radius: 50%;
      animation: socialPulse 2s infinite;
    }

    .social-dot.linkedin { background: #0077b5; animation-delay: 0s; }
    .social-dot.twitter { background: #1da1f2; animation-delay: 0.3s; }
    .social-dot.instagram { background: #e4405f; animation-delay: 0.6s; }

    @keyframes socialPulse {
      0%, 100% { transform: scale(1); opacity: 1; }
      50% { transform: scale(1.2); opacity: 0.8; }
    }

    .contact-line {
      display: flex;
      align-items: center;
      gap: 0.5rem;
      margin-top: 0.5rem;
      font-size: 0.875rem;
      color: #64748b;
    }

    .email-icon {
      font-size: 1rem;
      color: #667eea;
    }

    .gradient-logo {
      background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
      position: relative;
      overflow: hidden;
    }

    .gradient-logo::after {
      content: '';
      position: absolute;
      top: 0;
      left: -100%;
      width: 100%;
      height: 100%;
      background: linear-gradient(90deg, transparent, rgba(255,255,255,0.3), transparent);
      animation: logoShine 3s infinite;
    }

    @keyframes logoShine {
      0% { left: -100%; }
      100% { left: 100%; }
    }

    .signature-indicators {
      display: flex;
      gap: 0.75rem;
      margin-top: 1.5rem;
      justify-content: center;
    }

    .indicator {
      width: 12px;
      height: 12px;
      border-radius: 50%;
      background: rgba(102, 126, 234, 0.3);
      cursor: pointer;
      transition: all 0.3s ease;
    }

    .indicator.active {
      background: #667eea;
      transform: scale(1.2);
      box-shadow: 0 0 10px rgba(102, 126, 234, 0.5);
    }

    .name {
      font-weight: 600;
      color: #0f172a;
      margin-bottom: 0.25rem;
    }

    .title {
      color: #64748b;
      font-size: 0.875rem;
      margin-bottom: 0.25rem;
    }

    .company {
      color: #667eea;
      font-size: 0.875rem;
      font-weight: 500;
    }

    .signature-content.minimal {
      flex-direction: column;
      align-items: flex-start;
      gap: 0.5rem;
    }

    .signature-content.corporate {
      gap: 1rem;
    }

    .logo-placeholder {
      width: 40px;
      height: 40px;
      background: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
      border-radius: 8px;
    }

    /* Features Section */
    .features-enhanced {
      padding: 8rem 0;
      background: #fafbfc;
    }

    .section-header {
      text-align: center;
      max-width: 600px;
      margin: 0 auto 5rem;
    }

    .section-badge {
      display: inline-block;
      background: rgba(102, 126, 234, 0.1);
      color: #667eea;
      padding: 0.5rem 1rem;
      border-radius: 50px;
      font-size: 0.875rem;
      font-weight: 600;
      margin-bottom: 1rem;
    }

    .section-header h2 {
      font-size: 3rem;
      font-weight: 700;
      color: #0f172a;
      margin-bottom: 1rem;
    }

    .section-header p {
      font-size: 1.125rem;
      color: #64748b;
      line-height: 1.6;
    }

    .features-grid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
      gap: 2rem;
    }

    .feature-card {
      background: white;
      padding: 2rem;
      border-radius: 20px;
      box-shadow: 0 4px 20px rgba(0, 0, 0, 0.05);
      transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
      border: 1px solid #f1f5f9;
      position: relative;
      overflow: hidden;
      cursor: pointer;
    }

    .feature-card::before {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      background: linear-gradient(135deg, rgba(102, 126, 234, 0.05) 0%, rgba(118, 75, 162, 0.05) 100%);
      opacity: 0;
      transition: opacity 0.3s ease;
    }

    .feature-card:hover {
      transform: translateY(-8px) scale(1.02);
      box-shadow: 0 25px 60px rgba(0, 0, 0, 0.15);
      border-color: rgba(102, 126, 234, 0.2);
    }

    .feature-card:hover::before {
      opacity: 1;
    }

    .feature-hover-effect {
      position: absolute;
      top: var(--mouse-y, 50%);
      left: var(--mouse-x, 50%);
      width: 0;
      height: 0;
      background: radial-gradient(circle, rgba(102, 126, 234, 0.1) 0%, transparent 70%);
      border-radius: 50%;
      transform: translate(-50%, -50%);
      transition: all 0.3s ease;
      pointer-events: none;
    }

    .feature-card:hover .feature-hover-effect {
      width: 200px;
      height: 200px;
    }

    .animate-on-scroll {
      opacity: 0;
      transform: translateY(30px);
      transition: all 0.6s cubic-bezier(0.4, 0, 0.2, 1);
    }

    .animate-on-scroll.animate-in {
      opacity: 1;
      transform: translateY(0);
    }

    .animate-on-scroll:nth-child(1) { transition-delay: 0.1s; }
    .animate-on-scroll:nth-child(2) { transition-delay: 0.2s; }
    .animate-on-scroll:nth-child(3) { transition-delay: 0.3s; }
    .animate-on-scroll:nth-child(4) { transition-delay: 0.4s; }
    .animate-on-scroll:nth-child(5) { transition-delay: 0.5s; }
    .animate-on-scroll:nth-child(6) { transition-delay: 0.6s; }

    .feature-icon {
      width: 64px;
      height: 64px;
      border-radius: 16px;
      display: flex;
      align-items: center;
      justify-content: center;
      margin-bottom: 1.5rem;
      color: white;
      position: relative;
      overflow: hidden;
      transition: all 0.3s ease;
    }

    .feature-icon::after {
      content: '';
      position: absolute;
      top: 0;
      left: -100%;
      width: 100%;
      height: 100%;
      background: linear-gradient(90deg, transparent, rgba(255,255,255,0.3), transparent);
      transition: left 0.5s ease;
    }

    .feature-card:hover .feature-icon {
      transform: scale(1.1) rotate(5deg);
    }

    .feature-card:hover .feature-icon::after {
      left: 100%;
    }

    .feature-icon svg {
      width: 28px;
      height: 28px;
    }

    .gradient-blue { background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); }
    .gradient-purple { background: linear-gradient(135deg, #a855f7 0%, #ec4899 100%); }
    .gradient-green { background: linear-gradient(135deg, #10b981 0%, #059669 100%); }
    .gradient-orange { background: linear-gradient(135deg, #f59e0b 0%, #d97706 100%); }
    .gradient-pink { background: linear-gradient(135deg, #ec4899 0%, #be185d 100%); }
    .gradient-teal { background: linear-gradient(135deg, #14b8a6 0%, #0d9488 100%); }

    .feature-card h3 {
      font-size: 1.25rem;
