      cursor: pointer;
      display: flex;
      align-items: center;
      justify-content: space-between;
      position: relative;
    }

    .mobile-nav-link::before {
      content: '';
      position: absolute;
      left: 0;
      top: 0;
      bottom: 0;
      width: 0;
      background: linear-gradient(135deg, rgba(102, 126, 234, 0.1) 0%, rgba(118, 75, 162, 0.1) 100%);
      transition: width 0.3s ease;
    }

    .mobile-nav-link:hover {
      color: #667eea;
      padding-left: 1rem;
    }

    .mobile-nav-link:hover::before {
      width: 4px;
    }

    .mobile-nav-link i {
      font-size: 0.9rem;
      opacity: 0.5;
      transition: all 0.3s ease;
    }

    .mobile-nav-link:hover i {
      opacity: 1;
      transform: translateX(4px);
    }

    .mobile-nav-buttons {
      display: flex;
      flex-direction: column;
      gap: 1rem;
    }

    .btn-nav-login.mobile,
    .btn-nav-signup.mobile {
      width: 100%;
      justify-content: center;
      padding: 1rem 2rem;
      font-size: 1rem;
    }

    .btn-nav-login.mobile {
      border-radius: 12px;
    }

    .btn-nav-signup.mobile {
      border-radius: 12px;
    }

    /* Additional Sections */
    .templates-section,
    .pricing-section {
      padding: 6rem 0;
      background: white;
    }

    .templates-section {
      background: linear-gradient(135deg, #f8fafc 0%, #f1f5f9 100%);
    }



    /* Ripple Effect */
    .ripple-effect {
      position: absolute;
      border-radius: 50%;
      background: rgba(255, 255, 255, 0.6);
      transform: scale(0);
      animation: ripple 0.6s linear;
      pointer-events: none;
    }

    @keyframes ripple {
      to {
        transform: scale(4);
        opacity: 0;
      }
    }

    /* AI Chatbot */
    .ai-chatbot {
      position: fixed;
      bottom: 2rem;
      right: 2rem;
      z-index: 1000;
    }

    .chatbot-toggle {
      width: 60px;
      height: 60px;
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      border-radius: 50%;
      display: flex;
      align-items: center;
      justify-content: center;
      color: white;
      font-size: 1.5rem;
      cursor: pointer;
      box-shadow: 0 8px 25px rgba(102, 126, 234, 0.4);
      transition: all 0.3s ease;
    }

    .chatbot-toggle:hover {
      transform: scale(1.1);
      box-shadow: 0 12px 35px rgba(102, 126, 234, 0.6);
    }

    .chatbot-window {
      position: absolute;
      bottom: 80px;
      right: 0;
      width: 350px;
      height: 500px;
      background: white;
      border-radius: 20px;
      box-shadow: 0 20px 60px rgba(0, 0, 0, 0.2);
      display: flex;
      flex-direction: column;
      overflow: hidden;
      animation: slideUp 0.3s ease;
    }

    @keyframes slideUp {
      from {
        opacity: 0;
        transform: translateY(20px);
      }
      to {
        opacity: 1;
        transform: translateY(0);
      }
    }

    .chatbot-header {
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      color: white;
      padding: 1rem;
      display: flex;
      align-items: center;
      gap: 0.75rem;
    }

    .bot-avatar {
      width: 40px;
      height: 40px;
      background: rgba(255, 255, 255, 0.2);
