      border-radius: 18px;
      max-width: 80%;
      font-size: 0.875rem;
      line-height: 1.4;
    }

    .message-content.user {
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      color: white;
      margin-left: auto;
      border-radius: 18px 18px 4px 18px;
    }

    .bot-message .message-content {
      border-radius: 18px 18px 18px 4px;
    }

    .typing-indicator {
      display: flex;
      align-items: center;
      gap: 0.75rem;
    }

    .typing-dots {
      display: flex;
      gap: 0.25rem;
      padding: 0.75rem 1rem;
      background: #f1f5f9;
      border-radius: 18px 18px 18px 4px;
    }

    .typing-dots span {
      width: 6px;
      height: 6px;
      background: #94a3b8;
      border-radius: 50%;
      animation: typingDot 1.4s infinite;
    }

    .typing-dots span:nth-child(2) { animation-delay: 0.2s; }
    .typing-dots span:nth-child(3) { animation-delay: 0.4s; }

    @keyframes typingDot {
      0%, 60%, 100% { transform: translateY(0); }
      30% { transform: translateY(-10px); }
    }

    .chatbot-input {
      padding: 1rem;
      border-top: 1px solid #e2e8f0;
      display: flex;
      gap: 0.75rem;
      align-items: center;
    }

    .chat-input {
      flex: 1;
      border: 1px solid #e2e8f0;
      border-radius: 25px;
      padding: 0.75rem 1rem;
      font-size: 0.875rem;
      outline: none;
      transition: border-color 0.3s ease;
    }

    .chat-input:focus {
      border-color: #667eea;
    }

    .send-btn {
      width: 40px;
      height: 40px;
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      border: none;
      border-radius: 50%;
      color: white;
      display: flex;
      align-items: center;
      justify-content: center;
      cursor: pointer;
      transition: all 0.3s ease;
    }

    .send-btn:hover {
      transform: scale(1.1);
      box-shadow: 0 4px 15px rgba(102, 126, 234, 0.4);
    }

    /* Responsive */
    @media (max-width: 1024px) {
      .nav-links {
        gap: 1.5rem;
      }

      .btn-nav-login,
      .btn-nav-signup {
        padding: 0.6rem 1.2rem;
        font-size: 0.9rem;
      }
    }

    @media (max-width: 768px) {
      .nav-links {
        display: none;
