      .mobile-menu-toggle {
        display: block;
      }

      .navbar-enhanced {
        padding: 1rem 0;
      }

      .navbar-enhanced.scrolled {
        padding: 0.7rem 0;
      }

      .hero-title {
        font-size: 2.5rem;
      }

      .hero-actions {
        flex-direction: column;
        align-items: stretch;
        gap: 1rem;
      }

      .btn-primary-large,
      .btn-secondary-large {
        justify-content: center;
